@st.cache_resource
def load_dataset(path):
    try:
        ds = xr.open_dataset(path)

        ds = ds.sel(
            longitude=slice(-20, 10),
            latitude=slice(40, 15)
        )

        return ds
    except Exception as e:
        st.error(f"Erreur dataset : {e}")
        return None


