def get_basemap_layers_and_style(basemap_name, dark):
    base_style = "dark" if dark else "light"
    layers = []
    map_style = base_style

    if basemap_name == "OpenStreetMap":
        layers.append(
            pdk.Layer(
                "TileLayer",
                data="https://tile.openstreetmap.org/{z}/{x}/{y}.png",
                min_zoom=0,
                max_zoom=19,
                tile_size=256,
                pickable=False
            )
        )
        map_style = None
    elif basemap_name == "Terrain":
        layers.append(
            pdk.Layer(
                "TileLayer",
                data="https://stamen-tiles.a.ssl.fastly.net/terrain/{z}/{x}/{y}.png",
                min_zoom=0,
                max_zoom=18,
                tile_size=256,
                pickable=False
            )
        )
        map_style = None
    elif basemap_name == "Satellite":
        layers.append(
            pdk.Layer(
                "TileLayer",
                data="https://services.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
                min_zoom=0,
                max_zoom=18,
                tile_size=256,
                pickable=False
            )
        )
        map_style = None

    return layers, map_style

