def create_heatmap(df, radius, opacity):
    return pdk.Layer(
        "HeatmapLayer",
        data=df,
        get_position=["lon", "lat"],
        get_weight="value",
        radius_pixels=radius,
        opacity=opacity
    )

def create_scatter(df):
    return pdk.Layer(
        "ScatterplotLayer",
        data=df,
        get_position=["lon", "lat"],
        get_radius=4000,
        get_fill_color="[255 * value / value, 120, 80, 200]",
        pickable=True
    )

def create_hex(df, radius):
    return pdk.Layer(
        "HexagonLayer",
        data=df,
        get_position=["lon", "lat"],
        radius=radius * 1000,
        elevation_scale=50,
        extruded=True,
        pickable=True
    )

